const express=require('express');
const app=express();
const router = express.Router();

app.get("/:name",(req,res)=>{
    const {name} =req.params;
    res.write("hello world " + name);
    res.end();
});

app.use("/index",(req,res)=>{
    res.write('hello world');
    res.end();
});
app.use("/tom",(req,res)=>{
    res.write('hello world tom');
    res.end();
});
app.use("/",(req,res)=>{
    res.write('hello world barra nada');
    res.end();
});

app.get("/", (req, res) => {
    res.write("hello world");
    res.end();
  });
  
  app.get("/:name(\\w{3})", (req, res) => {
    var name = req.params.name;
    res.write("hello " + name);
    res.end();
  });
  router.get("/profile", (req, res) => {
    res.write("profile");
    res.end();
  });
  
  router.get("/status", (req, res) => {
    res.write("active");
    res.end();
  });
  
  app.use("/user", router);

app.listen(5000);








